haptic_utility_function.typescript:
// SwiftFill Haptic Utility (haptics.ts)

type HapticPattern = 'light' | 'medium' | 'heavy' | 'success' | 'warning' | 'error' | 'selection' | 'notification';

const HAPTIC_PATTERNS: Record<HapticPattern, number[]> = {
  light: [10],
  medium: [15],
  heavy: [25],
  success: [10, 50, 10],
  warning: [15, 30, 15],
  error: [25, 50, 25, 50, 25],
  selection: [5],
  notification: [50, 100, 50]
};

const AUDIO_PATTERNS: Record<HapticPattern, { frequency: number; duration: number; type: OscillatorType }> = {
  light: { frequency: 800, duration: 10, type: 'sine' },
  medium: { frequency: 600, duration: 15, type: 'sine' },
  heavy: { frequency: 400, duration: 25, type: 'square' },
  selection: { frequency: 1000, duration: 5, type: 'sine' },
  // ... (other patterns)
};

let audioContext: AudioContext | null = null;

function initAudioContext() {
  if (!audioContext && typeof AudioContext !== 'undefined') {
    audioContext = new AudioContext();
  }
}

function playAudioFeedback(pattern: HapticPattern) {
  if (!audioContext) return;

  const { frequency, duration, type } = AUDIO_PATTERNS[pattern];
  const oscillator = audioContext.createOscillator();
  const gainNode = audioContext.createGain();

  oscillator.type = type;
  oscillator.frequency.value = frequency;
  gainNode.gain.setValueAtTime(0.1, audioContext.currentTime);
  gainNode.gain.exponentialRampToValueAtTime(0.01, audioContext.currentTime + duration / 1000);

  oscillator.connect(gainNode);
  gainNode.connect(audioContext.destination);
  oscillator.start(audioContext.currentTime);
  oscillator.stop(audioContext.currentTime + duration / 1000);
}

export function triggerHaptic(pattern: HapticPattern, options?: { audio?: boolean; visual?: boolean }) {
  const opts = { audio: true, visual: true, ...options };

  // Try Vibration API (Android Chrome, Firefox Mobile)
  if ('vibrate' in navigator && window.matchMedia('(hover: none)').matches) {
    try {
      navigator.vibrate(HAPTIC_PATTERNS[pattern]);
    } catch (e) {
      console.warn('Vibration API failed:', e);
    }
  }

  // iOS fallback: Audio feedback (if enabled)
  if (opts.audio && /iPhone|iPad|iPod/.test(navigator.userAgent)) {
    initAudioContext();
    playAudioFeedback(pattern);
  }

  // Visual feedback handled by CSS (via data attribute)
  // Component sets data-haptic='pattern' temporarily
}

// React Hook
export function useHaptic() {
  return {
    trigger: triggerHaptic,
    patterns: Object.keys(HAPTIC_PATTERNS) as HapticPattern[]
  };
}

react_component_example.tsx:
// Example: Button with haptic feedback
import { triggerHaptic } from '@/lib/haptics';

function HapticButton({ children, hapticPattern = 'medium', ...props }: ButtonProps & { hapticPattern?: HapticPattern }) {
  const handleClick = (e: React.MouseEvent) => {
    triggerHaptic(hapticPattern);
    props.onClick?.(e);
  };

  return (
    <button
      {...props}
      onClick={handleClick}
      className={cn(
        'transition-all duration-150',
        'active:scale-95', // Visual feedback
        props.className
      )}
    >
      {children}
    </button>
  );
}

css_visual_feedback.css:
/* Visual feedback animations */
@keyframes haptic-pulse {
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
}

@keyframes haptic-shake {
  0%, 100% { transform: translateX(0); }
  25% { transform: translateX(-4px); }
  75% { transform: translateX(4px); }
}

[data-haptic='success'] {
  animation: haptic-pulse 300ms ease-out;
}

[data-haptic='error'] {
  animation: haptic-shake 200ms ease-in-out 2;
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  [data-haptic] {
    animation: none !important;
  }
}

implementation.css:
input[type='checkbox'][switch] {
  appearance: none;
  width: 66px;
  height: 44px;
  background: hsl(220 15% 88%);
  border-radius: 22px;
  position: relative;
  cursor: pointer;
  transition: background 200ms ease;
}

input[type='checkbox'][switch]:checked {
  background: hsl(215 85% 50%);
}

input[type='checkbox'][switch]::before {
  content: '';
  position: absolute;
  width: 40px;
  height: 40px;
  border-radius: 20px;
  background: white;
  top: 2px;
  left: 2px;
  transition: transform 200ms cubic-bezier(0.34, 1.56, 0.64, 1);
  box-shadow: 0 2px 4px hsl(0 0% 0% / 0.2);
}

input[type='checkbox'][switch]:checked::before {
  transform: translateX(22px);
}

implementation.javascript:
// Trigger haptic on toggle
const switchEl = document.querySelector('input[switch]');
switchEl.addEventListener('click', () => {
  // iOS 18+ Safari triggers native haptic automatically
  // Fallback for older iOS: audio feedback
  if (!CSS.supports('selector(input[switch])')) {
    playAudioFeedback('light');
  }
});

debouncing.example:
let lastHaptic = 0;
if (Date.now() - lastHaptic > 50) {
  triggerHaptic('light');
  lastHaptic = Date.now();
}
